# MAIN FLOW
#==============================================================

echo "<<< INITIALIZING KEGG KGML FILES DOWNLOAD..."

#set separator character
IFSAnt=$IFS
IFS=$'\n'

# go to work dir
cd ${WORKDIR}

if [[ ${DATA_TYPE} == "ec" ]]; then
    # Generate current organism folder name
    org_folder="${DATA_TYPE}"

    # Check if the browser folder exists
    if [ ! -d $org_folder ]; then
        mkdir -p $org_folder;
    fi

    # Enter into the org folder data
    cd $org_folder

    # Perform the KGML file download
    for lines in $(curl -s "http://rest.kegg.jp/list/pathway/ec"); do
        echo "Downloading $lines"
        file=$(echo $lines | awk '{split($1,a,":"); print a[2]}')
        #echo $file 
        curl -s "http://rest.kegg.jp/get/$file/kgml" -o ./"$file.xml"
    done
    
else
    # Iterate over the organism list
    for i in "${!ORG_LIST[@]}"; do
        export ${ORG_LIST[$i]}
        echo "***************"
        echo ${ORG_LIST[$i]}
        echo "***************"

        # Generate current organism folder name
        org_folder="${ORG_LIST[$i]}"

        # Check if the browser folder exists
        if [ ! -d $org_folder ]; then
            mkdir -p $org_folder;
        fi

        # Enter into the org folder data
        cd $org_folder

        # Perform the KGML file download
        for lines in $(curl -s "http://rest.kegg.jp/list/pathway/${ORG_LIST[$i]}"|head -10); do
            echo "Downloading $lines"
            file=$(echo $lines | awk '{split($1,a,":"); print a[2]}')
            #echo $file 
            curl -s "http://rest.kegg.jp/get/$file/kgml" -o ./"$file.xml"
        done

        #curl -s http://rest.kegg.jp/list/pathway/ | awk '{split($1,a,":"); print "curl -s http://rest.kegg.jp/get/"a[2]"/kgml -o ./"a[2]".xml"}' | bash

        # return to work dir
        cd ${WORKDIR}

    done

fi
echo "<<< PROCESS FINISHED SUCCESSFULLY!"

IFS=$IFSAnt

